    private void Boton_EntrarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_Boton_EntrarMouseClicked
        try {
            Menu_Principal Menu_Principal = new Menu_Principal();
            if (Archivo_Usuario_Contrase単a(Caja_Usuario.getText(), Caja_Contrasena.getText()) == true) {
                Menu_Principal.setLocationRelativeTo(null);
                Menu_Principal.setVisible(true);         
                dispose();
            } else {
                JOptionPane.showMessageDialog(null, "Usuario o Contrase単a Incorrecta. Por favor intentelo de nuevo.");
            }

        } catch (Exception ex) {
            JOptionPane.showMessageDialog(null, "Ocurrio un error:\n" + ex);
        }
    }//GEN-LAST:event_Boton_EntrarMouseClicked

