    public boolean Archivo_Usuario_Contrase単a(String Usuario, String Contrasena) {
        boolean llave_acceso = false;
        String sa ="";
        try {
            String linea = "";
            FileReader Archivo = new FileReader("Stores\\Usuario_Contrase単a.txt");
            BufferedReader Datos = new BufferedReader(Archivo);

            linea = Datos.readLine();
            while (linea != null) {
                if (linea.equals(Usuario)) {
                    linea = Datos.readLine();
                    if (linea.equals(Contrasena)) {
                        llave_acceso = true;
                    }
                }
                linea = Datos.readLine();
            }
            Archivo.close();
        } catch (Exception ex) {
            JOptionPane.showMessageDialog(null, "Ocurrio un error:\n" + ex);
        }
        return llave_acceso;
    }
    
